<?php /************************************************************************************************ * Program History :** Project Name     : OAS* Client Name      : CBI - Muhammad* Program Id       : OAS053* Program Name     : Edit Cost BT Related CA* Description      :* Environment      : PHP 5.4.4* Author           : Winni Oktaviani* Version          : 01.00.00* Creation Date    : 15-11-2014 23:20:00 ICT 2014** Update history     Re-fix date       Person in charge      Description* ** Copyright(C) [..]. All Rights Reserved*************************************************************************************************/if ( ! defined('BASEPATH')) exit('No direct script access allowed');class C_OAS053 extends MY_Controller {	function __construct()	{		parent::__construct();		$index	= $this->config->item('index_page');		$host	= $this->config->item('base_url');		$this->url = empty($index) ? $host : $host . $index . '/';		$this->load->model('m_oas053', 'cost_model');			}		function load_edit($costId)	{		$param['cost_info'] = $this->cost_model->get_cost_info($costId);		$param['status_edit'] = 1;		$this->load->view('v_oas053', $param);	}		function check_data()	{		if($this->input->post('ajax') == '1')		{			$response = 0;			$empl_id    			 = $this->input->post('employeeId');			$email 		 			 = $this->input->post('email');                     if($this->cost_model->count_employee_id($empl_id) != "0"){            	$response = $response + 1;            }            if($this->cost_model->count_employee_email($email) != "0"){            	$response = $response + 2;            }            echo $response;		}	}	function update_employee()	{		if($this->input->post('ajax') == '1')		{			$sbmt['this_user']		= $this->user['email'];			$sbmt['ID_COST']		= $this->input->post('cost_id');			$sbmt['DESTINATION']  	= $this->input->post('destination');            $sbmt['COST_IDR']		= $this->input->post('cost_idr');			$sbmt['COST_USD']		= $this->input->post('cost_usd');			$sbmt['COST_SGD']		= $this->input->post('cost_sgd');            $response = $this->cost_model->updateCostBTR($sbmt);            echo $response;		}	}}/* End of file c_oas053.php *//* Location: ./application/controllers/c_oas053.php */