<?php /************************************************************************************************ * Program History :** Project Name     : OAS* Client Name      : CBI - Muhammad* Program Id       : OAS103* Program Name     : Edit Tunjangan Per Karyawan* Description      :* Environment      : PHP 5.4.4* Author           : Winni Oktaviani* Version          : 01.00.00* Creation Date    : 13-02-2015 12:35:00 ICT 2015** Update history     Re-fix date       Person in charge      Description* ** Copyright(C) [..]. All Rights Reserved*************************************************************************************************/if ( ! defined('BASEPATH')) exit('No direct script access allowed');class C_OAS103 extends MY_Controller {	function __construct()	{		parent::__construct();		$index	= $this->config->item('index_page');		$host	= $this->config->item('base_url');		$this->url = empty($index) ? $host : $host . $index . '/';		$this->load->model('m_oas103', 'setting_model');			}		function load_edit($Id)	{		$param['employee_info'] = $this->setting_model->get_employee($Id);		$param['status_edit'] = 1;		$this->load->view('v_oas103', $param);	}		function update_tunjangan()	{		if($this->input->post('ajax') == '1')		{			$sbmt['this_user']		= $this->user['email'];			$sbmt['EMPLOYEE_ID']  	= $this->input->post('empid');			$sbmt['MEDIS']  		= $this->input->post('medis');            $sbmt['TRANSPORT']		= $this->input->post('transport');            $sbmt['TELKOM']			= $this->input->post('telkom');            $sbmt['PERIODE']		= $this->input->post('periode');            $response = $this->setting_model->updateTunjangan($sbmt);            echo $response;		}	}}/* End of file c_oas103.php *//* Location: ./application/controllers/c_oas103.php */