ype="text/javascript">function searchEmployee4Tunjangan(elm)	{				var employeeid  = $('#search_setting_tunjangan').val();		var url="c_oas104/search?";		if(employeeid != ''){			url += "employeeid="+employeeid+"&";		}		search_redirect(elm, encodeURI(host+url));	}	</script>